import React, { Component } from 'react';
import 'font-awesome/css/font-awesome.min.css';
import axios from "axios"; 

const regExp = RegExp(
    /^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/
)

const formValid = ({ isError, ...rest }) => {
    let isValid = false;

    Object.values(isError).forEach(val => {
        if (val.length > 0) {
            isValid = false
        } else {
            isValid = true
        }
    });

    Object.values(rest).forEach(val => {
        if (val === null) {
            isValid = false
        } else {
            isValid = true
        }
    });

    return isValid;
};

class Addcoin extends Component {
	constructor (props) {
		super(props);
		this.state = {
			name: '',
			symbol: '',
			market_cap: '',
			price: '',
			description: '',
			website: '',
			telegram: '',
			twitter: '',
			discord: '',
			isError: {
                name: '',
            }
		}
	}
	
	handleUserInput (e) {
		const name = e.target.name;
		const value = e.target.value;
		this.setState({[name]: value});
		
		let isError = { ...this.state.isError };
		
		switch (name) {
            case "name":
				alert(value);
				if(value == ''){
					isError.name = "Please enter coin name";
				} else if(value.length < 4){
					isError.name = "Atleast 4 characaters required";
				}else {
					isError.name = "";
				}
                break;
            default:
                break;
        }

        this.setState({
            isError,
            [name]: value
        })
	}
	
	handleSubmit = event => {
		event.preventDefault();
		if (formValid(this.state)) {
			const data = {
				name: this.state.name,
				symbol: this.state.symbol,
				market_cap: this.state.market_cap,
				price: this.state.price,
				description: this.state.description,
				website: this.state.website,
				telegram: this.state.telegram,
				twitter: this.state.twitter,
				discord: this.state.discord,
			}
			
			axios.post('http://localhost:8000/api/coins', { data }).then( res =>{
				console.log(res);
				console.log(res.data);
				//window.location = "/retrieve" //This line of code will redirect you once the submission is succeed
			})
		} else {
            console.log("Form is invalid!");
        }
		
	}
	
	render() {
		const { isError } = this.state;
		return (
			<div className="container margin-bottom-75">
				<div className="row">
					<div className="col-lg-12">
						<div id="utf_add_listing_part">     
							<form onSubmit = { this.handleSubmit } noValidate>
							<div className="add_utf_listing_section margin-top-45"> 
								<div className="utf_add_listing_part_headline_part">
									<h3><i className="fa fa-tag"></i> Coin info</h3>
								</div>  
								<div className="row with-forms">
									<div className="col-md-6">
										<h5>Name</h5>
										<input type="text" className={isError.name.length > 0 ? "is-invalid form-control" : "form-control"} name="name" id="name" placeholder="Coin Name" value={this.state.name} onChange={(event) => this.handleUserInput(event)} />
										{isError.name.length > 0 && ( <span className="invalid-feedback">{isError.name}</span> )}
									</div>
									<div className="col-md-6">
										<h5>Symbol</h5>                  
										<input type="text" name="symbol" id="symbol" placeholder="Symbol..." value={this.state.symbol} onChange={(event) => this.handleUserInput(event)} />
									</div>
								</div>   
								<div className="row with-forms">
									<div className="col-md-6">
										<h5>Market Cap in USD</h5>
										<input type="number" className="search-field" name="market_cap" id="market_cap" placeholder="Market Cap in USD" value={this.state.market_cap} onChange={(event) => this.handleUserInput(event)} />
									</div>
									<div className="col-md-6">
										<h5>Price in USD</h5>                  
										<input type="number" name="price" id="price" placeholder="Price in USD" value={this.state.price} onChange={(event) => this.handleUserInput(event)} />
									</div>
								</div>             
								<div className="col-md-12">
									<h5>Description</h5>
									<textarea name="description" cols="40" rows="3" id="description" placeholder="Description..." spellcheck="true" onChange={(event) => this.handleUserInput(event)} >{this.state.description}</textarea>
								</div> 
							</div>
							<div className="add_utf_listing_section margin-top-45"> 
								<div className="utf_add_listing_part_headline_part">
									<h3><i className="fa fa-list"></i> Links</h3>
								</div>              
								<div className="row with-forms">
									<div className="col-md-6">
										<h5>Website</h5>
										<input type="text" placeholder="Website" name="website" value={this.state.website} onChange={(event) => this.handleUserInput(event)} />
									</div>	
									<div className="col-md-6">
										<h5>Telegram</h5>
										<input type="text" placeholder="Telegram" name="telegram" value={this.state.telegram} onChange={(event) => this.handleUserInput(event)} />
									</div>
									<div className="col-md-6">
										<h5>Twitter</h5>
										<input type="text" placeholder="Twitter" name="twitter" value={this.state.twitter} onChange={(event) => this.handleUserInput(event)} />
									</div>
									<div className="col-md-6">
										<h5>Discord</h5>
										<input type="text" placeholder="Discord" name="discord" value={this.state.discord} onChange={(event) => this.handleUserInput(event)} />
									</div>				  				 
								</div>                
							</div>
							<button type="submit" className="button preview"><i className="fa fa-arrow-circle-right"></i> Add Coin</button> 
							</form>
						</div>
					</div>        
				</div>
			</div>
		);
	}
}

export default Addcoin;
