//import logo from './logo.svg';
import React, { useReducer, useState } from 'react';
import './App.css';

const formReducer = (state, event) => {
	if(event.reset) {
		return {
			apple: '',
			count: 0,
			name: '',
			name: '',
			name: '',
			'gift-wrap': false,
		}
	}
	return {
		...state,
		[event.name]: event.value
	}
}


function App() {
	const [formData, setFormData] = useReducer(formReducer, { count: 100,});
	const [submitting, setSubmitting] = useState(false);
	const handleSubmit = event => {
		event.preventDefault();
		setSubmitting(true);

		fetch('http://localhost:8000/coins', {
			method: 'POST',
			body: JSON.stringify({ formData }),
			headers: { 'Content-Type': 'application/json' },
		}).then(res => res.json()).then(json => setFormData(json.formData))

		setTimeout(() => {
			setSubmitting(false);
			setFormData({
				reset: true
			})
		}, 3000)
	}

	const handleChange = event => {
		const isCheckbox = event.target.type === 'checkbox';
		setFormData({
			name: event.target.name,
			value: isCheckbox ? event.target.checked : event.target.value,
		});
	}


	return (
		/* <div className="App">
			<header className="App-header">
				<img src={logo} className="App-logo" alt="logo" />
				<p>
					Edit <code>src/App.js</code> and save to reload.
				</p>
				<a className="App-link" href="https://reactjs.org" target="_blank" rel="noopener noreferrer" >Learn React</a>
			</header>
		</div> */
		<div className="wrapper">
			<h1>Add Coins</h1>
			{submitting && 
			<div>
				You are submitting the following:
				<ul>
					{Object.entries(formData).map(([name, value]) => (
					<li key={name}><strong>{name}</strong>: {value.toString()}</li>
					))}
				</ul>
			</div>
			}

			<form onSubmit={handleSubmit}>
				<fieldset disabled={submitting} >
					<label>
						<p>Name</p>
						<input name="name" onChange={handleChange} value={formData.name || ''} />
					</label>
					<label>
						<p>Symbol</p>
						<input name="symbol" onChange={handleChange} value={formData.symbol || ''} />
					</label>
					<label>
						<p>Description</p>
						<input name="description" onChange={handleChange} value={formData.description || ''} />
					</label>
				</fieldset>
				<fieldset disabled={submitting} >
					<label>
						<p>Apples</p>
						<select name="apple" onChange={handleChange} value={formData.apple || ''}>
							<option value="">--Please choose an option--</option>
							<option value="fuji">Fuji</option>
							<option value="jonathan">Jonathan</option>
							<option value="honey-crisp">Honey Crisp</option>
						</select>
					</label>
					<label>
						<p>Count</p>
						<input type="number" name="count" onChange={handleChange} step="1" value={formData.count || ''} />
					</label>
					<label>
						<p>Gift Wrap</p>
						<input type="checkbox" name="gift-wrap" onChange={handleChange} checked={formData['gift-wrap'] || false} disabled={formData.apple !== 'fuji'} />
					</label>
				</fieldset>
				<button type="submit" disabled={submitting} >Submit</button>
			</form>
		</div>
	);
}

export default App;
