import React, { Component } from 'react';
import Modal from './Modal/Modal';
import axios from 'axios';
import CoinImg from '../img/1.jpg';
import galaxy from '../img/galaxy.gif';
import magicdoge from '../img/magicdoge.gif';

import ReactTimeAgo from 'react-time-ago';
import TimeAgo from 'javascript-time-ago';

import en from 'javascript-time-ago/locale/en.json';
import ru from 'javascript-time-ago/locale/ru.json';

TimeAgo.addDefaultLocale(en);

class CoinDetails extends Component {
	
	constructor(props) {
		super(props);
		this.state = {
			coins: [],
			show: false,
			setShow: false,
			addVoteData: {
				id: "",
				count: "",
			},
			isLoading: false,
		};
	}
	
	componentDidMount() {
		
		axios.get('http://localhost:8000/api/coinsPromoted/')
		.then(res => {
			this.setState({
				coins: res.data
			});
			//console.log(this.state.coins);
		})
		.catch((error) => {
			console.log(error);
		})
	}
	
	modalOpen = (id, count) => {
		this.setState({ 
			addVoteData: { id, count },
			show: true
		});
	}
	
	modalClose (e) {
		this.setState({ show: false });
	}
	
	updateVote = () => {
		let {
			id,
			count
        } = this.state.addVoteData;
		
		this.setState({
			isLoading: true,
        });
		
		axios.post("https://listingapp.cryptolisting.com/admin/api/addVoteCount", {
			id,
			count,
		}).then((response) => {
			axios.get('http://localhost:8000/api/coinsPromoted/')
			.then(res => {
				this.setState({
					coins: res.data
				});
				//console.log(this.state.coins);
			})
			.catch((error) => {
				console.log(error);
			})
            this.setState({
				show: false,
				addVoteData: { id, count },
				isLoading:false,
            });
		}).catch((error) => {
            this.setState({isLoading:false})
            console.log(error.response);
		});
	};
	
	render() {
		return (
			<div class="container">
				<div class="row utf_sticky_main_wrapper">
					<div class="col-lg-12 col-md-12">
						<div id="utf_listing_overview" class="utf_listing_section">
							<h3 class="utf_listing_headline_part margin-top-30 margin-bottom-30">Coin added Successfully</h3>
							<p>Your coin will be listed in our website as soon as after admin approval. Thanks you!</p>
							<div class="social-contact">
								<a href="/" class="facebook-link"><i class="fa fa-arrow-left"></i> Back to Home</a>
								<a href="/addcoin" class="twitter-link"><i class="fa fa-plus"></i> Add more coins</a>
							</div>
						</div>
					</div>
				</div>
			</div>
		);
	}
}

export default CoinDetails;
