import React, { Component } from 'react';
import { Button } from "reactstrap";
import Addvote from './Addvote';
import axios from 'axios';
import CoinImg from '../img/1.jpg';

import ReactTimeAgo from 'react-time-ago';
import TimeAgo from 'javascript-time-ago'

import en from 'javascript-time-ago/locale/en.json';
import ru from 'javascript-time-ago/locale/ru.json';

TimeAgo.addDefaultLocale(en);

class Promoted extends Component {
	
	constructor(props) {
		super(props);
		this.state = {
			coins: [],
			addVoteData: {
				id: "",
				votecount: "",
			},
			addVoteModal: false,
		};
	}
	
	componentDidMount() {
		axios.get('http://localhost:8000/api/coinsApproved/')
		.then(res => {
			this.setState({
				coins: res.data
			});
			//console.log(this.state.coins);
		})
		.catch((error) => {
			console.log(error);
		})
	}
	
	toggleAddVoteModal = () => {
        this.setState({
			addVoteModal: !this.state.addVoteModal,
		});
	};
	
	addVoteCount = ( id, votecount ) => {
		this.setState({
			addVoteData: { id, votecount },
			addVoteModal: !this.state.addVoteModal,
		});
	};
  
	render() {
		const { addVoteData } = this.state;
		
		let studentsDetails = [];
		if (students.length) {
        studentsDetails = students.map((student) => {
			return (
            <tr key={student.id}>
              <td>{student.id}</td>
              <td>{student.first_name}</td>
              <td>{student.last_name}</td>
              <td>{student.full_name}</td>
              <td>{student.email}</td>
              <td>{student.phone}</td>
              <td>
                <Button
                  color="success"
                  className="mr-3"
                  size="sm"
                  onClick={() =>
                    this.editStudent(
                      student.id,
                      student.first_name,
                      student.last_name,
                      student.email,
                      student.phone
                    )
                  }
                >
                  Edit
                </Button>
                <Button
                  color="danger"
                  size="sm"
                  onClick={() => this.deletStudent(student.id)}
                >
                  Delete
                </Button>
              </td>
            </tr>
          );
        });
		}
		
		return (
			<div className="col-lg-12 col-md-12 mb-4">
				<div className="utf_dashboard_list_box">
					<h4 style={{textAlign: 'center'}}>Promoted Coins</h4>
					<div className="dashboard-list-box table-responsive invoices with-icons">
						{/* Model for Edit Studnet Record */}
						<Addvote toggleAddVoteModal={this.toggleAddVoteModal} addVoteModal={this.state.addVoteModal} addVoteCount={this.addVoteCount} addVoteData={addVoteData} />
						
						<table className="table table-hover">
							<thead>
								<tr>
									<th>#</th>
									<th>Image</th>
									<th>Coin name</th>
									<th>Symbol</th>
									<th>Chain</th>
									<th>Launch</th>
									<th>Votes</th>
								</tr>
							</thead>
							<tbody>
								{ this.state.coins.map(function (res, i) {
									return (<tr>
												<td>{ i+1 }</td>
												<td><img alt="" className="img-fluid rounded-circle shadow-lg" src={'http://localhost:8000/storage/coin_logo/'+ res.logo } width="50" height="50" /></td>
												<td>{res.name}</td>
												<td>{res.symbol}</td>
												<td>{res.chain}</td>
												<td><span className="badge badge-pill badge-primary text-uppercase"><ReactTimeAgo date={res.launch} /></span></td>
												<td><Button onClick={() => this.addVoteCount(res._id, '1') } className="button gray"> {res.votes} </Button></td>
									</tr>);
								})}
							</tbody>
						</table>
					</div>
				</div>
			</div>
		);
	}
}

export default Promoted;
